import React, { Component } from 'react'
import PropTypes from 'prop-types'

export class TodoItem extends Component {
    render() {
        return (
            <div>
                <p>{this.props.todo.title}</p>
            </div>
        )
    }
}

//PropTypes
Todos.propTypes = {
    todos: PropTypes.array.isRequired
}
export default TodoItem
